#!/bin/sh
export GRAPH_HYPER_OPT_PATH="$(dirname "$(dirname "$(readlink -fm "$0")")")"
python ga_ray_tune.py $GRAPH_HYPER_OPT_PATH/data/vectorized/gender/tr.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/gender/vl.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/gender/ts.h5 --training_iteration 1 --num_samples 100 --run-name hpo_gender_v1 --search-alg hyperopt --use-wandb 1>ray_log_hpo_gender_v1.log 2>ray_log_hpo_gender_v1_err.log
