python ga_ray_tune.py $GRAPH_HYPER_OPT_PATH/data/vectorized/gender_imitation/tr.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/gender_imitation/vl.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/gender_imitation/ts.h5 --training_iteration 1 --num_samples 100 --run-name hpo_gender_imitation_v1 --search-alg hyperopt 1>ray_log_hpo_gender_imitation_v1.log 2>ray_log_hpo_gender_imitation_v1_err.log
python ga_ray_tune.py $GRAPH_HYPER_OPT_PATH/data/vectorized/age_imitation/tr.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/age_imitation/vl.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/age_imitation/ts.h5 --training_iteration 1 --num_samples 100 --run-name hpo_age_imitation_v1 --search-alg hyperopt 1>ray_log_hpo_age_imitation_v1.log 2>ray_log_hpo_age_imitation_v1_err.log
python ga_ray_tune.py $GRAPH_HYPER_OPT_PATH/data/vectorized/style_imitation/tr.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/style_imitation/vl.h5 $GRAPH_HYPER_OPT_PATH/data/vectorized/style_imitation/ts.h5 --training_iteration 1 --num_samples 100 --run-name hpo_style_imitation_v1 --search-alg hyperopt 1>ray_log_hpo_style_imitation_v1.log 2>ray_log_hpo_style_imitation_v1_err.log
